    //           console.log('Search by  ', data);
    //         },
    //         function (err) {
    //           console.error(err);
    //         }
    //       );
    //   },[action]);

    return (
        <div className = "player">
        <div className = "player__body">

            <Sidebar switcher= {switcher}/>
            <Body spotify = {spotify} action = {action}/>
            <Footer/>
        </div>

        </div>
    )
}

export default Player
