import React,{useState,useEffect} from 'react'
import "./MassPlaylist.css";
import TrackItem from "./TrackItem.js";
export default function MassPlaylist({spotify}) {
    const [search, setSearch] = useState("");
    const [searchResults, setSearchResults] = useState([])
    const [modalOn,setModalOn] = useState(false);

    function handleChange(event)
    {
       setSearch(event.target.value);

    }
    function handleSubmit(event){
       setSearchResults([]);
        const searchList = search.split("\n");
        console.log("list",searchList);
        searchList.forEach((track) => {

            spotify.searchTracks(track).then(res => {
                console.log(res.tracks.items[0])
                const element = res.tracks.items[0];
                const smallestAlbumImage = element.album.images.reduce(
                    (smallest, image) => {
                        if (image.height < smallest.height) return image
                        return smallest;
                    },
                    element.album.images[0]
                )
                const data = { 

                    artist: element.artists[0].name,
                    title: element.name,
                    uri: element.uri,
                    albumUrl: smallestAlbumImage.url,


                }
                setSearchResults(searchResults => [...searchResults,data]);
            })

        })
        event.preventDefault();
    }
    useEffect(() =>{
        console.log("searchreasults",searchResults);
    },[searchResults])

    function handleCreatePlaylist(event) {
        console.log("pressed");
        if(searchResults.length == 0){return;}

        //Create a private playlist
        spotify.createPlaylist('aarreol', { 'name':"test",'description': 'My description', 'public': false })
            .then(function (data) {
                console.log('Created playlist!');
            }, function (err) {
                console.log('Something went wrong!', err);
            });
            event.preventDefault();
    }

    return (
        <div className = "container">

            <button className="submit PS" onClick ={handleCreatePlaylist} >Create Playlist</button>
            <form onSubmit = {handleSubmit}className = "search">

          <textarea className = "textBox" value={search} onChange={handleChange} />

                <input className = "submit" type="submit" value="Submit" />

            </form> 

            <div className="flex-grow-1 my-2" style={{ overflowY: "auto" }}>
                {searchResults.map(track => (
                    <TrackItem
                        track={track}
                        key={track.uri}

                    />
                ))}

            </div>
           {modalOn?modalon:modalo}
        </div>
    )
}
