import React, {useState,useEffect} from 'react'
import "./SongLookup.css"
import {Container,Form} from 'react-bootstrap';
import useAuth from './useAuth';
import SpotifyWebApi  from "spotify-web-api-js";

//const spotifyApi = new SpotifyWebApi({clientId:'6ca42bd62f0542959053535545c73611',});

function SongLookup({spotify}) {

    // const accessToken = useAuth(spotify);
    // console.log(accessToken);
    const [search, setSearch] = useState();
    const [searchResults, setSearchResults] = useState([])

    useEffect(() => {
        if (!accessToken) return;
        spotifyApi.setAccessToken(accessToken);
    }, [accessToken]);

    useEffect(() => {
        if (!search) return setSearchResults([])
        let cancel = false;
        spotifyApi.searchTracks(search).then(res => {
            if (cancel) return;
            setSearchResults(
                res.body.tracks.items.map(track => {
                    const smallestAlbumImage = track.album.images.reduce(
                        (smallest, image) => {
                            if (image.height < smallest.height) return image
                            return smallest;
                        },
                        track.album.images[0]
                    )

                    return {
                        artist: track.artists[0].name,
                        title: track.name,
                        uri: track.uri,
                        albumUrl: smallestAlbumImage.url,
                    }
                })
            )
        })

        return () => (cancel = true)


    }, [search])

    return (
        <div className="SongLookup">
            <h3>Type in a Song, Artist, or Album</h3>
            <Container className="d-flex flex-column py-2" style={{ height: "100vh" }}>
                <Form.Control
                    type="search"
                    placeholder="Search Songs/Artists"
                    value={search}
                    onChange={e => setSearch(e.target.value)}
                />


            </Container>
        </div>
    )
}

export default SongLookup
