import SpotifyWebApi  from "spotify-web-api-js";
import {useDataLayerValue} from "./ServiceProvider"

const spotify = new SpotifyWebApi();

function App() {
 //const [token,setToken] = useState(null);
 const [{user,token},dispatch] = useDataLayerValue();
 const [button,setButton] = useState(0);
 useEffect(()=>{
  const hash = getTokenFromURL();
  window.location.hash = "";

  const _token = hash.access_token;
  if(_token){
    //setToken(_token);
    dispatch({type: "SET_TOKEN", token:_token, })
    spotify.setAccessToken(_token);

    spotify.getMe().then(user => {
      dispatch({type: 'SET_USER', user:user,})

    })
  }

 });
 useEffect(() => {
  // Update the document title using the browser API
  console.log();
  spotify.searchTracks('Bad Bunn',{ limit: 5 }).then(
      function (data) {
        console.log('Search by  ', data);
      },
      function (err) {
        console.error(err);
      }
    );
},[]);
const updateButton = () =>{
    console.log("clicked Once");
};
 console.log("usererer", user);
 console.log("usertoken44",token);

  return (

    <div className="app">
    <button onClick ={updateButton}className = "button"></button>
    {
      token ? <Player spotify={spotify}></Player>:<Login/>
    }

    </div>
  );
}

export default App;
